// src/components/HangmanGame.jsx
import React, { useEffect, useState, useCallback } from 'react';
import '../styles/HangmanGame.css';

const MAX_TRIES = 7;

export default function HangmanGame() {
    const [words, setWords] = useState([]);
    const [selectedWord, setSelectedWord] = useState('');
    const [displayedWord, setDisplayedWord] = useState([]);
    const [wrongGuesses, setWrongGuesses] = useState([]);
    const [triesLeft, setTriesLeft] = useState(MAX_TRIES);
    const [wins, setWins] = useState(0);
    const [losses, setLosses] = useState(0);
    const [points, setPoints] = useState(0);
    const [gameOver, setGameOver] = useState(false);
    const [letter, setLetter] = useState('');
    const [swissTime, setSwissTime] = useState('');

    const startGame = useCallback((wordArray = words) => {
        const word = wordArray[Math.floor(Math.random() * wordArray.length)];
        setSelectedWord(word);
        setDisplayedWord(Array(word.length).fill('_'));
        setWrongGuesses([]);
        setTriesLeft(MAX_TRIES);
        setGameOver(false);
        setLetter('');
    }, [words]);

    // Wortliste beim ersten Laden holen
    useEffect(() => {
        fetch('/word_list.txt')
            .then(res => res.text())
            .then(data => {
                const wordArray = data.split('\n').map(w => w.trim().toLowerCase()).filter(Boolean);
                setWords(wordArray);
            });
    }, []);

    // Starte Spiel sobald Wörter verfügbar sind
    useEffect(() => {
        if (words.length > 0) {
            startGame(words);
        }
    }, [words, startGame]);

    useEffect(() => {
        const interval = setInterval(updateSwissTime, 1000);
        return () => clearInterval(interval);
    }, []);

    const updateSwissTime = () => {
        const options = {
            timeZone: 'Europe/Zurich',
            hour: '2-digit',
            minute: '2-digit',
            second: '2-digit',
            hour12: false
        };
        setSwissTime(new Date().toLocaleTimeString('de-CH', options));
    };

    const handleGuess = () => {
        if (gameOver || !letter) return;

        const l = letter.toLowerCase();
        setLetter('');

        if (wrongGuesses.includes(l) || displayedWord.includes(l)) return;

        if (selectedWord.includes(l)) {
            const newDisplayed = displayedWord.map((char, i) =>
                selectedWord[i] === l ? l : char
            );
            setDisplayedWord(newDisplayed);

            if (!newDisplayed.includes('_')) {
                setGameOver(true);
                setWins(wins + 1);
                setPoints(points + 10);
            }
        } else {
            const newTries = triesLeft - 1;
            setWrongGuesses([...wrongGuesses, l]);
            setTriesLeft(newTries);

            if (newTries === 0) {
                setGameOver(true);
                setLosses(losses + 1);
                setPoints(Math.max(0, points - 5));
            }
        }
    };

    const getImageSrc = () => {
        const level = MAX_TRIES - triesLeft + 1;
        return `/img/level${level}.png`;
    };

    return (
        <div className="game-container">
            <div id="clock-container">
                <p className="swiss-time-label">Swiss Time</p>
                <p className="current-time">{swissTime}</p>
            </div>

            <div id="score-display">
                <p id="total-points" className="score-text">Points: {points}</p>
                <div id="win-loose-count">
                    <p id="win-count" className="score-text">{wins}</p>
                    <p>/</p>
                    <p id="loss-count" className="score-text">{losses}</p>
                </div>
            </div>

            <h1>Hangman - Game</h1>
            <div className="word-display">{displayedWord.join(' ')}</div>

            <div className="input-container">
                <label htmlFor="letter-input">Enter a letter:</label>
                <input
                    type="text"
                    id="letter-input"
                    maxLength="1"
                    value={letter}
                    onChange={(e) => setLetter(e.target.value)}
                />
                <button className="submit-btn" onClick={handleGuess}>Guess</button>
            </div>

            <div className="wrong-guesses">Wrong letters: {wrongGuesses.join(', ')}</div>

            <div className="hangman-drawing">
                <img src={getImageSrc()} alt="Hangman Level" />
            </div>

            <div className="message">
                {gameOver
                    ? (triesLeft === 0 ? `Lost! The word was: ${selectedWord}` : 'You won!')
                    : `Tries left: ${triesLeft}`}
            </div>

            <button className="restart-btn" onClick={() => startGame(words)}>Restart</button>
        </div>
    );
}
